import { useEffect,useState } from "react";
import Card from "../UI/Card";
import classes from "./AvailibleMeals.module.css";
import MealItem from "./MealItem/MealItem";

// const DUMMY_MEALS = [
//   {
//     id: "m1",
//     name: "Sushi",
//     description: "Finest fish and veggies",
//     price: 22.99,
//   },
//   {
//     id: "m2",
//     name: "Schnitzel",
//     description: "A german specialty!",
//     price: 16.5,
//   },
//   {
//     id: "m3",
//     name: "Barbecue Burger",
//     description: "American, raw, meaty",
//     price: 12.99,
//   },
//   {
//     id: "m4",
//     name: "Green Bowl",
//     description: "Healthy...and green...",
//     price: 18.99,
//   },
// ];

const AvailibleMeals = () => {
    const [meals, setMeals] = useState([]);
    const [isLoading, setIsLoading]=useState(true);
  //   const mealsList = DUMMY_MEALS.map((meal) => <li>{meal.name}</li>);
 useEffect(()=>{
    // setIsLoading(true);
    const fetchMeals = async () => {
      const response= await fetch('https://meals-ce9b7-default-rtdb.firebaseio.com/meals.json');
      const responseData = await response.json();//response data eshte objekt, neve na duhet array, andaj e transformojmë:

      const loadedMeals= [];
      for(const key in responseData){
        loadedMeals.push({
            id:key,
            name:responseData[key].name,
            description:responseData[key].description,
            price:responseData[key].price
        })
      }
      setMeals(loadedMeals);//Përdorim state në mënyrë që komponenta të bëhët re-render pasi të kryhet fetch..
      setIsLoading(false);
    };
    fetchMeals();
 },[]);

 if (isLoading){
    return <section className={classes.MealsLoading}>
        <p>Loading...</p>
    </section>
 }

  const mealsList = meals.map((meal) => (
    <MealItem 
    id={meal.id}
    key={meal.id} 
    name={meal.name} 
    description={meal.description} 
    price={meal.price}
    />
  ));
  return (
    <section className={classes.meals}>
      <Card>
        <ul>{mealsList}</ul>
      </Card>
    </section>
  );
};

export default AvailibleMeals;
